import java.util.*;

public class Q_10951 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a=0;
		int b=0;
		while(sc.hasNext()) {
			a=sc.nextInt();
			b=sc.nextInt();
			System.out.println(a+b);
		}
	}

}
