import java.util.*;

public class Q_11021 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = 0;
		int a, b;
		n = sc.nextInt();
		for (int i = 1; i <= n; i++) {
			a = sc.nextInt();
			b = sc.nextInt();
			System.out.printf("Case #%d: %d" + "\n", i, a + b);
		}

	}

}
